mapper.update:
UPDATE user_ground
SET is_attending = 1
WHERE user_id = #{userId}
  AND ground_id = #{groundId}

UPDATE user
SET point = (select point FROM user WHERE id = #{userId}) - (select deposit FROM ground WHERE id = #{groundId})
WHERE id = #{userId}

mapper.select:
SELECT max_capacity
FROM ground
WHERE id = #{groundId}

SELECT deposit
FROM ground
WHERE id = #{groundId}

SELECT point
FROM user
WHERE id = #{userId}

SELECT count(*)
FROM user_ground
WHERE user_id = #{userId}
  AND ground_id = #{groundId}
  AND is_attending = 0

mapper.update:
UPDATE user_ground
SET is_attending = 0
WHERE user_id = #{userId}
  AND ground_id = #{groundId}

UPDATE user
SET point = (select point FROM user WHERE id = #{userId}) + (select deposit FROM ground WHERE id = #{groundId})
WHERE id = #{userId}
